DROP TABLE bet_option_org_statistics IF EXISTS;
DROP TABLE bet_option_player_statistics IF EXISTS;
DROP TABLE opposite IF EXISTS;

CREATE TABLE bet_option_org_statistics (id bigint GENERATED BY DEFAULT AS IDENTITY, trade_code varchar(64), pre_id bigint, org_id bigint, org_account varchar(32), org_nick_name varchar(32), player_lottery_id bigint, player_id bigint, lottery_id bigint, lottery_name varchar(32), handicap_id bigint, sub_handicap_id bigint, sub_handicap_name varchar(32), play_method_id bigint, play_method_name varchar(32), bet_option_id bigint, bet_option_name varchar(32), issue_number varchar(32), principal numeric(18, 4), bet_num int, valid_amount numeric(18, 4), share_ratio float, actual_share_ratio float, share_commission_amount numeric(18, 4), commission_ratio float, commission_amount numeric(18, 4), commission_profit_amount numeric(18, 4), earn_commission_amount numeric(18, 4), merit_pre_amount numeric(18, 4), payable_pre_amount numeric(18, 4), profit_loss_amount numeric(18, 4), fly_share_amount numeric(18, 4), non_fly_share_amount numeric(18, 4), non_fly_loss_amount numeric(18, 4), non_fly_profit_loss_amount numeric(18, 4), fly_commission_amount numeric(18, 4), fly_loss_amount numeric(18, 4), fly_profit_amount numeric(18, 4), earn_odds_amount numeric(18, 4), owner_type varchar(16), status varchar(16), is_immediate_player varchar(4), create_time datetime, PRIMARY KEY (id));
CREATE TABLE bet_option_player_statistics (id bigint GENERATED BY DEFAULT AS IDENTITY, trade_code varchar(64), player_lottery_id bigint, player_id bigint, lottery_id bigint, lottery_name varchar(32), handicap_id bigint, sub_handicap_id bigint, sub_handicap_name varchar(32), play_method_id bigint, play_method_name varchar(32), bet_option_id bigint, bet_option_name varchar(32), issue_number varchar(32), principal numeric(18, 4), bet_num int, player_commission_ratio float, player_commission numeric(18, 4), player_profit_loss numeric(18, 4), owner_type varchar(16), status varchar(16), create_time datetime, PRIMARY KEY (id));
CREATE TABLE opposite (id bigint GENERATED BY DEFAULT AS IDENTITY, pre_id bigint, org_id bigint, org_account varchar(32), org_nick_name varchar(32), player_lottery_id bigint, player_id bigint, lottery_id bigint, handicap_id bigint, sub_handicap_id bigint, play_method_id bigint, play_method_name varchar(32), bet_option_id bigint, bet_option_name varchar(32), issue_number varchar(32), odds varchar(10), bet_amount numeric(18, 4), estimated_amount numeric(18, 4), supplement_amount numeric(18, 4), fly_type varchar(16), type varchar(16), owner_type varchar(16), PRIMARY KEY (id));
